package ch02.sec01;

public class VarEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//변수 선언
		int value;//4바이트
		
		//연산 결과를 변수 result의 초기값으로 대입
		int result= value + 10;//value값이 설정되지 않아 나는 오류
		
		//변수 result 값을 읽고 콘솔에 출력
		System.out.println(result);
	}

}
